mapper.select:
SELECT




FROM account


        AND id = #{id}


        AND username = #{username}


        AND `name` = #{name} AND email = #{email}


        AND username = #{username} AND email = #{email}

mapper.update:
UPDATE account
SET update_date = CURRENT_TIMESTAMP

    , username = #{username}


    , password = #{password}


    , name = #{name}


    , email = #{email}


    , role = #{role}


    , resign_yn = #{resignYn}

WHERE id = #{id}
